import React, {Component} from 'react';
import { Input, Upload, message, Layout, Button, Table } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import axios from 'axios';
import "./Main.css";


const { TextArea } = Input;
const { Header, Content, Footer } = Layout;


const columns = [
    {
        title: 'Review',
        dataIndex: 'review',
        width: '80%',
    },
    {
        title: 'Result',
        dataIndex: 'result',
        width: '20%',
    }
]


const getRandomuserParams = params => {
    return {
      results: params.pagination.pageSize,
      page: params.pagination.current,
      ...params,
    };
};

class Main extends Component {

    state = {
        selectedFile: null,
        review: null,
        loading: true,
        data: [],
        reviews: [],
        pagination: {
            current: 1,
            pageSize: 10,
        }
    }

    fileChangeHandler = (info, event) => {
        if (info.file.status !== 'uploading') {
            console.log(info.file, info.fileList);
        }
        if (info.file.status === 'done') {
            message.success(`${info.file.name} file uploaded successfully`);
        } else if (info.file.status === 'error') {
            message.error(`${info.file.name} file upload failed.`);
        }
    }

    reviewChangeHandler = (event) => {
        this.setState({review: event.target.value})
    }

    postFileHandler = () => {
        if (this.state.review) {
            axios.post('http://3.139.179.60/singleinference', this.state.review, {timeout: 5 * 60 * 1000})
            .then(response => {
                console.log(response);
            });
        } else if (this.state.selectedFile) {
            this.setState({loading: true})
            axios.post('http://3.139.179.60/inference', this.state.selectedFile, {timeout: 5 * 60 * 1000})
            .then(response => {
                console.log(response);
                const newData = []
                this.state.reviews.map((comment, idx) => {
                    let dataPoint = {
                        key: idx,
                        review: comment,
                        result: response[idx]
                    };
                    newData.push(dataPoint)
                })
                this.setState({
                    data: newData,
                    loading: false})
            });
        }
    }

    componentDidMount () {
        const { pagination } = this.state;
        axios.get( 'http://3.139.179.60/ping' )
        .then(response => {
            console.log(response);
        });
    }

    render () {
        return (
            <section id="test" className="Test">
                <div>
                    <Content style={{ maxWidth: '800px', margin: 'auto'}}>
                        <TextArea
                            rows={4}
                            name='review'
                            onChange={this.reviewChangeHandler}
                            style={{width: '800px'}} />
                        <p style={{marginTop: "30px"}}><strong>Or you can also choose to upload a file. (Only .txt files are acceptable)</strong> </p>
                        <Upload 
                            accept=".txt" 
                            beforeUpload={file => {
                                const reader = new FileReader();
                                const reviews = []
                                reader.onload = e => {
                                    reviews.push(e.target.result)
                                };
                                reader.readAsText(file);
                                this.setState({selectedFile: file,
                                    reviews: reviews})

                                // Prevent upload
                                return false;
                            }}
                            onChange={this.fileChangeHandler} 
                            name='file'>
                            <Button icon={<UploadOutlined />}>Click to Upload</Button>
                        </Upload>
                    </Content>
                    <Button type="primary" shape="round" onClick={this.postFileHandler}>Submit</Button>
                </div>

            </section>
        );
    }

}

export default Main;
