import React, {Component} from 'react';
import { Input, Upload, message, Layout, Button, Table } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import axios from 'axios';
import "./Main.css";


const { TextArea } = Input;
const { Header, Content, Footer } = Layout;


const columns = [
    {
        title: 'Review',
        dataIndex: 'review',
        width: '80%',
    },
    {
        title: 'Result',
        dataIndex: 'result',
        width: '20%',
    }
]

const reviews = [];
let fileReader;

const getRandomuserParams = params => {
    return {
      results: params.pagination.pageSize,
      page: params.pagination.current,
      ...params,
    };
};

class Main extends Component {

    state = {
        selectedFile: null,
        review: null,
        loading: true,
        data: [],
        reviews: [],
        pagination: {
            current: 1,
            pageSize: 10,
        }
    }

    fileReadHandler = () => {
        const content = fileReader.result;
        console.log(content);
        reviews.push(content);
    }

    fileChangeHandler = (file) => {
        this.setState({selectedFile: file});
        fileReader  = new FileReader();
        fileReader.onloadend = this.fileReadHandler;
        fileReader.readAsText(event.target.files[0]);
        this.setState({reviews: reviews})
        console.log(this.state.reviews)
    }

    reviewChangeHandler = (event) => {
        this.setState({review: event.target.value})
    }

    postFileHandler = () => {
        if (this.state.review) {
            axios.post('http://3.139.179.60/singleinference', this.state.review, {timeout: 5 * 60 * 1000})
            .then(response => {
                console.log(response);
            });
        } else if (this.state.selectedFile) {
            this.setState({loading: true})
            axios.post('http://3.139.179.60/inference', this.state.selectedFile, {timeout: 5 * 60 * 1000})
            .then(response => {
                console.log(response);
                this.setState({

                })
            });
        }
    }

    componentDidMount () {
        const { pagination } = this.state;
        axios.get( 'http://3.139.179.60/ping' )
        .then(response => {
            console.log(response);
        });
    }

    render () {
        return (
            <section id="test" className="Test">
                <div>
                    <Content style={{ maxWidth: '800px', margin: 'auto'}}>
                        <TextArea
                            rows={4}
                            name='review'
                            onChange={this.reviewChangeHandler}
                            style={{width: '800px'}} />
                        <p style={{marginTop: "30px"}}><strong>Or you can also choose to upload a file. (Only .txt files are acceptable)</strong> </p>
                        <Upload accept=".txt" onChange={(event) => {this.fileChangeHandler(event.target.file[0])}}>
                            <Button icon={<UploadOutlined />}>Click to Upload</Button>
                        </Upload>
                    </Content>
                    <Button type="primary" shape="round" onClick={this.postFileHandler}>Submit</Button>
                </div>

            </section>
        );
    }

}

export default Main;
